import kotlin.test.assertEquals

fun main() {
    assertEquals("mjqjpqmgbljsphdztnvjfqwrcgsmlb".firstMarkerAfter(), 7)
    assertEquals("bvwbjplbgvbhsrlpgdmjqwftvncz".firstMarkerAfter(), 5)
    assertEquals("nppdvjthqldpwncqszvftbrmjlhg".firstMarkerAfter(), 6)
    assertEquals("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg".firstMarkerAfter(), 10)
    assertEquals("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw".firstMarkerAfter(), 11)
    println(PUZZLE6_INPUT.firstMarkerAfter())

    assertEquals("mjqjpqmgbljsphdztnvjfqwrcgsmlb".firstMarkerAfter(size = 14), 19)
    assertEquals("bvwbjplbgvbhsrlpgdmjqwftvncz".firstMarkerAfter(size = 14), 23)
    assertEquals("nppdvjthqldpwncqszvftbrmjlhg".firstMarkerAfter(size = 14), 23)
    assertEquals("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg".firstMarkerAfter(size = 14), 29)
    assertEquals("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw".firstMarkerAfter(size = 14), 26)
    println(PUZZLE6_INPUT.firstMarkerAfter(size = 14))
}

private fun String.firstMarkerAfter(size: Int = 4): Int {
    return this.windowed(size = size)
        .mapIndexed { index, s -> if (s.toSet().size == size) index else null }
        .filterNotNull()
        .first() + size
}


private const val PUZZLE6_INPUT =
    """"""
